var gulp = require('gulp');
var htmlone = require('../index');
console.log(htmlone)


gulp.task('htmloneDaily', function() {
    gulp.src(['./*.html'])
        .pipe(htmlone({
            destDir: './dest',
            cssminify: false,
            jsminify: true,
            appName: 'app-myproperty',
            appVersion: '0.0.0',
            publish: false 
        }))
        .pipe(gulp.dest('./dest/daily'));
});
gulp.task('htmloneOnline', ['htmloneDaily'], function() {
    gulp.src(['./*.html'])
        .pipe(htmlone({
            destDir: './dest',
            cssminify: false,
            jsminify: true,
            appName: 'app-myproperty',
            appVersion: '0.0.0',
            publish: true
        }))
        .pipe(gulp.dest('./dest/online'));
});

gulp.task('default', ['htmloneOnline'])